import { Box, makeStyles, Paper, Typography } from "@material-ui/core";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { Doughnut } from "react-chartjs-2";

interface CountType {
  branchCount: number;
  clientCount: number;
  contractCount: number;
  contactCount: number;
}

const useStyles = makeStyles({
  container: {
    display: "flex",
    flexDirection: "row",
  },
  root: {
    minWidth: "275px",
    minHeight: "120px",
    margin: "10px",
    display: "flex",
    padding: "0 10px",
    alignItems: "center",
    justifyContent: "space-between",
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});
const Count = () => {
  const classes = useStyles();
  const [count, setCount] = useState<CountType>(null);

  useEffect(() => {
    axios
      .get("/api/count/getDashboardData")
      .then(({ data }) => {
        setCount(data);
        console.log(data);
      })
      .catch((err) => console.log(err));
  }, []);

  const data = {
    labels: ["Red", "Blue", "Yellow", "Green", "Purple", "Orange"],
    datasets: [
      {
        label: "# of Votes",
        data: [12, 19, 3, 5, 2, 3],
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(153, 102, 255, 0.2)",
          "rgba(255, 159, 64, 0.2)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(153, 102, 255, 1)",
          "rgba(255, 159, 64, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };
  return (
    <Box m={5} className={classes.container}>
      <Paper className={classes.root} elevation={3}>
        <Typography
          color="textSecondary"
          gutterBottom
          component="h1"
          variant="h5"
        >
          Branch
        </Typography>
        <Typography component="span" variant="h4" color="textPrimary">
          {count?.branchCount}
        </Typography>
      </Paper>
      <Paper className={classes.root} elevation={3}>
        <Typography
          color="textSecondary"
          gutterBottom
          component="h1"
          variant="h5"
        >
          Client
        </Typography>
        <Typography component="span" variant="h4" color="textPrimary">
          {count?.clientCount}
        </Typography>
      </Paper>
      <Paper className={classes.root} elevation={3}>
        <Typography
          color="textSecondary"
          gutterBottom
          component="h1"
          variant="h5"
        >
          ContactPerson
        </Typography>
        <Typography component="span" variant="h4" color="textPrimary">
          {count?.contractCount}
        </Typography>
      </Paper>
      <Paper className={classes.root} elevation={3}>
        <Typography
          color="textSecondary"
          gutterBottom
          component="h1"
          variant="h5"
        >
          Contract
        </Typography>
        <Typography component="span" variant="h4" color="textPrimary">
          {count?.contractCount}
        </Typography>
      </Paper>

      <div>
        <Doughnut data={chartData} />
      </div>
    </Box>
  );
};

export default Count;
