import { Box, Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, TextField } from "@material-ui/core"
import { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import axios from 'axios';

interface FormData{
    company_name: string,
    company_address: string,
    client_name: string,
    email: string,
    phone: number,
}

const Client = () => {
    const [open, setOpen] = useState<boolean>(false);

    const { register, handleSubmit, setValue, formState: { errors } } = useForm<FormData>();

    useEffect(() => {
        axios.get('api/client');
    });

    const handleClient = (data) => {
        // console.log(data);
         axios.post('api/client',data)
            .then((response) => {console.log(response);})
            .catch((error) => {console.log(error)});
    }


    return (

        <Box m={5}>

            <Button variant="outlined" color="primary" onClick={() => setOpen(!open)} >
                Add Client
            </Button>

            <Dialog open={open} onClose={() => setOpen(!open)} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">Add New Client</DialogTitle>

                <form onSubmit={handleSubmit(handleClient)}>
                    <DialogContent dividers={true}>
                        <TextField margin="normal" size="medium" variant="outlined" autoFocus label="Company Name" type="text" fullWidth {...register("company_name",{required:true})} />
                        <TextField margin="normal" size="medium" variant="outlined" label="Client Name" type="text" fullWidth {...register("client_name",{required:true})} />
                        <TextField margin="normal" size="medium" variant="outlined" label="Company Address" type="text" fullWidth {...register("company_address",{required:true})}/>
                        <TextField margin="normal" size="medium" variant="outlined" label="Company Email" type="email" fullWidth {...register("email",{required:true})}/>
                        <TextField margin="normal" size="medium" variant="outlined" label="Company Phone" type="number" fullWidth {...register("phone",{required:true})}/>
                    </DialogContent>
                    <DialogActions>
                        <Button color="primary" variant="contained" type="submit">
                            Save
                        </Button>
                        <Button color="secondary" variant="contained" onClick={() => setOpen(!open)}>
                            Cancel
                        </Button>
                    </DialogActions>
                </form>

            </Dialog>



        </Box>
    )
}

export default Client
