import { Box, TextField, makeStyles, Button, Typography, InputAdornment, IconButton } from '@material-ui/core'
import { Visibility, VisibilityOff } from '@material-ui/icons'
import { useState } from 'react';
import { Link } from 'react-router-dom'
import { useForm } from "react-hook-form";

const useStyles = makeStyles((theme) => ({
    box: {
        textAlign: 'center',
        marginTop: '-140px',
        display: 'flex',
        flexDirection: 'column',
        background: 'white',
        borderRadius: '10px',
        border: '5px solid #fafafa',
        [theme.breakpoints.down('sm')]: {
            width: '60%',
        },
    },
    title: {
        marginBottom: '20px'
    },
    input: {
        marginBottom: '10px',
    },
}));


const Login = () => {

    const [showPassword, setShowPassword] = useState<boolean>(false);

    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const onSubmit = data => console.log(data);

    const handleClickShowPassword = () => {
        console.log('show pass');
        console.log(showPassword);
        setShowPassword(!showPassword);
    };

    // const handleMouseDownPassword = (event: React.MouseEvent<HTMLButtonElement>) => {
    //     event.preventDefault();
    // };

    const classes = useStyles();

    return (
        <Box className={classes.box} p={5} width="25%">

            <form onSubmit={handleSubmit(onSubmit)}>

                <Typography className={classes.title} variant="h4">Register</Typography>
                <TextField id="outlined-basic" label="Full Name" variant="outlined" className={classes.input} />
                <TextField id="outlined-basic" label="Email" variant="outlined" className={classes.input} />
                <TextField id="outlined-basic" label="Password" variant="outlined" className={classes.input} 
                    type={showPassword ? 'text' : 'password'}
                    InputProps={{
                        endAdornment:(
                        <InputAdornment position="end">
                            <IconButton
                            aria-label="toggle password visibility"
                            onClick={handleClickShowPassword}
                            // onMouseDown={handleMouseDownPassword}
                            >
                            {showPassword ? <Visibility /> : <VisibilityOff />}
                            </IconButton>
                        </InputAdornment>
                        )
                    }}
                />
                <TextField id="outlined-basic" label="Confirm Password"  variant="outlined" className={classes.input}
                    type={showPassword ? 'text' : 'password'}
                    InputProps={{ 
                        endAdornment:(
                            <InputAdornment position="end">
                            <IconButton
                                aria-label="toggle password visibility"
                                onClick={handleClickShowPassword}
                                // onMouseDown={handleMouseDownPassword}
                            >
                                {showPassword ? <Visibility /> : <VisibilityOff />}
                            </IconButton>
                            </InputAdornment>
                        )
                    }}
                />
                <Button className={classes.title} size="large" variant="contained" color="primary" style={{ marginTop: '20px' }}>Submit</Button>

            </form>

            <Typography variant="body2">Already Registered <Link to="/login">Login</Link></Typography>

        </Box>
    )
}

export default Login
