import { Box, makeStyles, Paper, Typography } from "@material-ui/core";
import axios from "axios";
import React, { useEffect, useState } from "react";

interface CountType {
  branchCount: number;
  clientCount: number;
  contractCount: number;
  contactCount: number;
}

const useStyles = makeStyles({
  container: {
    display: "flex",
    flexDirection: "row",
  },
  root: {
    minWidth: "275px",
    margin: "10px",
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});
const Count = () => {
  const classes = useStyles();
  const [count, setCount] = useState<CountType>(null);

  useEffect(() => {
    axios
      .get("/api/count/getDashboardData")
      .then(({ data }) => {
        setCount(data);
        console.log(data);
      })
      .catch((err) => console.log(err));
  }, []);
  return (
    <Box m={5} className={classes.container}>
      <Paper className={classes.root} variant="outlined" elevation={3}>
        <Typography
          color="textSecondary"
          gutterBottom
          component="h1"
          variant="h5"
        >
          Branch
          <Typography component="span" variant="h4" color="textPrimary">
            {count?.branchCount}
          </Typography>
        </Typography>
      </Paper>
      <Paper className={classes.root} variant="outlined" elevation={3}>
        <Typography
          color="textSecondary"
          gutterBottom
          component="h1"
          variant="h5"
        >
          Client
          <Typography component="span" variant="h4" color="textPrimary">
            {count?.clientCount}
          </Typography>
        </Typography>
      </Paper>
      <Paper className={classes.root} variant="outlined" elevation={3}>
        <Typography
          color="textSecondary"
          gutterBottom
          component="h1"
          variant="h5"
        >
          ContactPerson
          <Typography component="span" variant="h4" color="textPrimary">
            {count?.contractCount}
          </Typography>
        </Typography>
      </Paper>
      <Paper className={classes.root} variant="outlined" elevation={3}>
        <Typography
          color="textSecondary"
          gutterBottom
          component="h1"
          variant="h5"
        >
          Contract
          <Typography component="span" variant="h4" color="textPrimary">
            {count?.contractCount}
          </Typography>
        </Typography>
      </Paper>
    </Box>
  );
};

export default Count;
