import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, MenuItem, Select, TextField } from "@material-ui/core"
import { useState } from "react";
import useSWR, { useSWRConfig } from 'swr';
import fetcher from '../../../utils/fetcher';

const Contract = () => {

    const { mutate } = useSWRConfig()

    const [openDialog,setOpenDialog] = useState<boolean>(false);
    const [isEditMode,setIsEditMode] = useState<boolean>(false);
    const [selectValue,setSelectValue] = useState<string>('');

    const { data:clients, error: clientError} = useSWR('api/client/name', fetcher);

    if (clientError) return <div>failed to load</div>
    if (!clients) return <div>loading...</div>


    return (
        <Box m={5}>

            <Button color="primary" variant="outlined" onClick={() => setOpenDialog(true)}>Add Contract</Button>

            <Dialog open={openDialog} onClose={() => {}} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">
                    {isEditMode ? 'Update Contract' : 'Add Contract'}
                </DialogTitle>
                <DialogContent>

                    <Select {...register("client_id", { required: "Client is required!" })}
                        labelId="label" id="select" fullWidth variant="outlined"
                        defaultValue={selectValue}
                    >
                        {clients && clients?.map(client => (
                            <MenuItem key={client._id} value={client._id}> {client.company_name} </MenuItem>
                        ))}
                    </Select>

                    <TextField
                        autoFocus
                        margin="dense"
                        id="name"
                        label="Email Address"
                        type="email"
                        fullWidth
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => {}} color="primary" variant="contained">
                        {isEditMode ? 'Update' : 'Save'}
                    </Button>
                    <Button onClick={() => setOpenDialog(false)} color="secondary" variant="contained">
                        Close
                    </Button>
                </DialogActions>
            </Dialog>


        </Box>
    )
}

export default Contract
