import { makeStyles } from '@material-ui/core';

export default makeStyles((theme) => ({
    sidebar: {
        textAlign: 'center',
        display: 'flex',
        // alignItems:'center',
        // justifyContent: 'center',
        flexDirection: 'column',
        background: 'white',
        borderRadius: '10px',
        border: '5px solid #fafafa',
        [theme.breakpoints.down('sm')]: {
            width: '60%',
        },
    },
}));
