import { makeStyles,Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, FormControl, InputLabel, MenuItem, Select, TextField } from "@material-ui/core"
import { useState } from "react";
import useSWR, { useSWRConfig } from 'swr';
import fetcher from '../../../utils/fetcher';
import { useForm } from "react-hook-form";

const useStyles = makeStyles((theme) => ({
    formControl:{
        margin:theme.spacing(1)
    }
}))

const Contract = () => {

    const classes = useStyles();
    const { mutate } = useSWRConfig()

    const [openDialog,setOpenDialog] = useState<boolean>(false);
    const [isEditMode,setIsEditMode] = useState<boolean>(false);
    const [selectValue,setSelectValue] = useState<string>('');

    const { register, handleSubmit, watch, formState: { errors } } = useForm();

    const onSubmit = data => console.log(data);


    const { data:clients, error: clientError} = useSWR('api/client/name', fetcher);

    if (clientError) return <div>failed to load</div>
    if (!clients) return <div>loading...</div>


    return (
        <Box m={5}>

            <Button color="primary" variant="outlined" onClick={() => setOpenDialog(true)}>Add Contract</Button>

            <form onSubmit={handleSubmit(onSubmit)}>
                <Dialog open={openDialog} onClose={() => {}} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">
                        {isEditMode ? 'Update Contract' : 'Add Contract'}
                    </DialogTitle>
                    <DialogContent>

                        {/* <FormControl  fullWidth> */}
                            <InputLabel id="label" >Client</InputLabel>
                            <Select {...register("client_id", { required: "Client is required!" })}
                                labelId="label" id="select"  variant="outlined"
                                defaultValue={selectValue} autoFocus style={{marginBottom:"10px"}}
                            >
                                {clients && clients?.map(client => (
                                    <MenuItem key={client._id} value={client._id}> {client.company_name} </MenuItem>
                                ))}
                            </Select>
                        {/* </FormControl> */}

                        {/* <FormControl  fullWidth> */}
                            <InputLabel id="payment">Payment Status</InputLabel>
                            <Select {...register('payment_status',{required:'Payment Status is required!'})}
                                labelId="payment" fullWidth variant="outlined" >
                                <MenuItem value="paid">Paid</MenuItem>
                                <MenuItem value="notpaid">Not Paid</MenuItem>
                            </Select>
                        {/* </FormControl> */}

                        <TextField
                            {...register('contract_sign_date',{required:'Contract Sign Date is required!'})}
                            fullWidth
                            margin="normal"
                            variant="outlined"
                            label="Contract Sign Date"
                            type="date"
                            defaultValue="2020-01-01"

                        />
                        <TextField
                            {...register('contract_end_date', { required: 'Contract End Date is required!' })}
                            fullWidth
                            margin="normal"
                            variant="outlined"
                            label="Contract End Date"
                            type="date"
                            defaultValue="2022-01-01"
                        />

                    </DialogContent>
                    <DialogActions>
                        <Button onClick={() => {}} color="primary" variant="contained" type="submit">
                            {isEditMode ? 'Update' : 'Save'}
                        </Button>
                        <Button onClick={() => setOpenDialog(false)} color="secondary" variant="contained">
                            Close
                        </Button>
                    </DialogActions>
                </Dialog>
            </form>


        </Box>
    )
}

export default Contract
