import { Box, TextField, makeStyles, Button, Typography, InputAdornment, IconButton } from '@material-ui/core'
import { Visibility, VisibilityOff } from '@material-ui/icons'
import { useState } from 'react';
import { Link } from 'react-router-dom'
import { useForm } from "react-hook-form";
import * as yup from 'yup';

const useStyles = makeStyles((theme) => ({
    box: {
        textAlign: 'center',
        marginTop: '-140px',
        display: 'flex',
        flexDirection: 'column',
        background: 'white',
        borderRadius: '10px',
        border: '5px solid #fafafa',
        [theme.breakpoints.down('sm')]: {
            width: '60%',
        },
    },
    title: {
        marginBottom: '20px'
    },
    input: {
        marginBottom: '10px',
    },
    error:{
        textAlign:'left',
        color:'red',
        marginBottom: '10px',
    },
}));


const Login = () => {

    const [showPassword, setShowPassword] = useState<boolean>(false);

    // react form hook
    const validationSchema = Yup.object().shape({
        password: Yup.string()
            .required('Password is required')
            .min(6, 'Password must be at least 6 characters'),
        confirmPassword: Yup.string()
            .required('Confirm Password is required')
            .oneOf([Yup.ref('password')], 'Passwords must match')

    });
    const formOptions = { resolver: yupResolver(validationSchema) };


    const { register, handleSubmit, watch, formState: { errors } } = useForm(formOptions);
    const onSubmit = (data) => {
        console.log(data);
    }


    const handleClickShowPassword = () => {
        console.log('show pass');
        console.log(showPassword);
        setShowPassword(!showPassword);
    };

    // const handleMouseDownPassword = (event: React.MouseEvent<HTMLButtonElement>) => {
    //     event.preventDefault();
    // };

    const classes = useStyles();

    return (
            // <form onSubmit={handleSubmit(onSubmit)}>
            <Box className={classes.box} p={5} width="25%" component="form" onSubmit={handleSubmit(onSubmit)}>

                <Typography className={classes.title} variant="h4">Register</Typography>
                <TextField {...register("name", { required: true, maxLength:40,minLength:5})} id="outlined-basic" label="Full Name" variant="outlined" className={classes.input} />
                <Typography component="span" className={classes.error}>
                    {errors.name?.type === 'required' && "Full name is required"}
                    {errors.name?.type === 'minLength' && "Full name should not be less than 5 characters"}
                </Typography>

                <TextField {...register("email",{required:true, maxLength:30})} id="outlined-basic" label="Email" variant="outlined" className={classes.input} />
                <Typography component="span" className={classes.error}>
                    {errors.name?.type === 'required' && "Email is required"}
                    {errors.name?.type === 'minLength' && "Email shouldnot be greater than 30"}
                </Typography>

                <TextField 
                    {...register("password", {required:true, minLength: { value: 5, message: 'Password should be longer than 5' }, maxLength: { value: 20,message:'Password Too Big' } })} 
                        id="outlined-basic" label="Password" variant="outlined" className={classes.input}
                        type={showPassword ? 'text' : 'password'}
                        InputProps={{
                            endAdornment:(
                            <InputAdornment position="end">
                                <IconButton
                                aria-label="toggle password visibility"
                                onClick={handleClickShowPassword}
                                // onMouseDown={handleMouseDownPassword}
                                >
                                {showPassword ? <Visibility /> : <VisibilityOff />}
                                </IconButton>
                            </InputAdornment>
                            )
                        }}
                    />
                <Typography component="span" className={classes.error}>
                    {errors.password?.type === 'required' && "Password is required"}
                    {errors.password?.type === 'minLength' && errors.password.message }
                    {errors.password?.type === 'maxLength' && errors.password.message }
                </Typography>
                <TextField {...register("confirmPassword", { required: true, maxLength: 30, minLength: 5 })} id="outlined-basic" label="Confirm Password"  variant="outlined" className={classes.input}
                    type={showPassword ? 'text' : 'password'}
                    InputProps={{ 
                        endAdornment:(
                            <InputAdornment position="end">
                            <IconButton
                                aria-label="toggle password visibility"
                                onClick={handleClickShowPassword}
                                // onMouseDown={handleMouseDownPassword}
                            >
                                {showPassword ? <Visibility /> : <VisibilityOff />}
                            </IconButton>
                            </InputAdornment>
                        )
                    }}
                />
                {errors.confirmPassword?.type === 'required' && "Password is required"}
                {errors.confirmPassword?.type === 'minLength' && "password sho"}
                {errors.confirmPassword?.type === 'maxLength' && ""}

                <Button type="submit" className={classes.title} size="large" variant="contained" color="primary" style={{ marginTop: '20px' }}>Submit</Button>


            <Typography variant="body2">Already Registered <Link to="/login">Login</Link></Typography>

        </Box>
    )
}

export default Login
