import { makeStyles,Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, FormControl, InputLabel, MenuItem, Select, TextField, TableContainer, Table, TableHead, TableRow, TableBody, Paper, Chip } from "@material-ui/core"
import { useState } from "react";
// utils
import fetcher from '../../../utils/fetcher';
// library
import useSWR, { useSWRConfig } from 'swr';
import { useForm,Controller } from "react-hook-form";
import axios from "axios";
import { useSnackbar } from 'notistack';
import Moment from 'react-moment';
// icons
import { Delete, PanTool } from "@material-ui/icons";
// styles
import StyledTableCell from "../Partials/StyledTableCell";
import useStyles from '../Partials/CommonStyles'
import ConfirmDelete from "../Partials/ConfirmDelete";

const useCommonStyles = makeStyles((theme) => ({
    formControl:{
        margin:theme.spacing(1)
    }
}))

interface ClientType {
    _id: string,
    company_name: string,
}
interface ContractType{
    _id?:string,
    client_id:string,
    contract_end_date:string,
    contract_sign_date:string,
    payment_status:string,
}

interface ContractEditType{
    client_id: ClientType,
    contract_end_date: string,
    contract_sign_date: string,
    payment_status: string,
}

const Contract = () => {

    // styles config
    const commonClasses = useCommonStyles();
    const classes = useStyles();
    const { enqueueSnackbar } = useSnackbar();

    // swr config
    const { mutate } = useSWRConfig();
    const CLIENT_API = 'api/client/name';
    const CONTRACT_API = 'api/contract/';

    // state
    const [openDialog,setOpenDialog] = useState<boolean>(false);
    const [isEditMode,setIsEditMode] = useState<boolean>(false);
    const [deleteId,setDeleteId] = useState<string>(null);
    const [openDeleteDialog,setOpenDeleteDialog] = useState<boolean>(false);
    const [selectPaymentValue, setSelectPaymentValue] = useState<string>('');
    const [selectClientValue, setSelectClientValue] = useState<string>('');

    // hook form
    const { register, handleSubmit, control, setValue, reset, formState: { errors } } = useForm<ContractType>({
        defaultValues: {
            contract_sign_date: '2021-11-12',
            contract_end_date: '2022-11-12',
        }
    });

    // FETCH DATA
    const { data: clients, error: clientError } = useSWR(CLIENT_API, fetcher);
    const { data: contracts, error: contractError } = useSWR(CONTRACT_API, fetcher);

    if (clientError) return <div>failed to load</div>
    if (!clients) return <div>loading...</div>
    if (contractError) return <div>failed to load</div>
    if (!contracts) return <div>loading...</div>
    console.log('whole contract');
    console.log(contracts);


    const onSubmit = (data: ContractType) => {
        console.log(data);
        if(!isEditMode)
            axios.post(CONTRACT_API, data)
                .then(({ data }) => { mutate(CONTRACT_API);setOpenDialog(false); enqueueSnackbar(data.success_msg,{variant:'success'}) })
                .catch((err) => { console.log(err); })
        else
            {
                console.log(data);
                // axios.patch(CONTRACT_API + data)
            }
    }

    const handleEdit = (row: ContractEditType) => {
        console.log(row);
        reset({
            contract_sign_date: row.contract_sign_date.split('T')[0],
            contract_end_date: row.contract_end_date.split('T')[0],
        });
        setSelectPaymentValue(row.payment_status);
        setSelectClientValue(row.client_id._id);
        setOpenDialog(true);
        setIsEditMode(true);
    }

    const handleDelete = () => {
        axios.delete(CONTRACT_API + deleteId)
            .then(({ data }) => { console.log(data); mutate(CONTRACT_API); setOpenDeleteDialog(false); enqueueSnackbar(data.success_msg,{variant:'success'})})
            .catch((err) => { console.log(err);})
    }


    return (
        <Box m={5}>

            <Button color="primary" variant="outlined" onClick={() => setOpenDialog(true)}>Add Contract</Button>

            {/* add/update dialog/model */}
            <Dialog open={openDialog} onClose={() => {}} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">
                    {isEditMode ? 'Update Contract' : 'Add Contract'}
                </DialogTitle>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <DialogContent>

                        {/* <InputLabel id="label" className={commonClasses.formControl}>Client</InputLabel> */}

                        {/* <Select {...register("client_id", { required: "Client is required!" })}
                            labelId="label" id="select" variant="outlined" fullWidth
                            // defaultValue={selectValue}  
                        >
                            {clients && clients?.map(client => (
                                <MenuItem key={client._id} value={client._id}> {client.company_name} </MenuItem>
                            ))}
                        </Select> */}

                        {/* <FormControl>
                            <InputLabel htmlFor="trinity-select">
                                Client
                            </InputLabel>
                            <Controller
                                control={control}
                                name="client_id"
                                render={({
                                    field: { onChange, onBlur, value, name, ref },
                                    fieldState: { invalid, isTouched, isDirty, error },
                                }) => (
                                    <Select id="trinity-select" fullWidth variant="outlined">
                                        {clients && clients.map((client) => (
                                            <MenuItem key={client._id} value={client._id}>
                                                {client.company_name}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                )}

                            />
                        </FormControl> */}

                        {/* <InputLabel id="payment" className={commonClasses.formControl}>Payment Status</InputLabel> */}
                        <TextField select {...register('client_id')} style={{marginBottom:'10px'}}
                            label="Client" fullWidth variant="outlined" defaultValue={selectClientValue}>
                            <MenuItem value="">Choose</MenuItem>
                            {clients && clients.map((client) => (
                                <MenuItem key={client._id} value={client._id}>
                                    {client.company_name}
                                </MenuItem>
                            ))}
                        </TextField>

                        {/*  {...register('payment_status')} */}
                        <TextField select inputProps={register('payment_status')}
                            label="Payment Status" fullWidth variant="outlined" defaultValue={selectPaymentValue}>
                            <MenuItem value="">Choose</MenuItem>
                            <MenuItem value="paid">Paid</MenuItem>
                            <MenuItem value="notpaid">Not Paid</MenuItem>
                        </TextField>

                        <TextField
                            {...register('contract_sign_date',{required:'Contract Sign Date is required!'})}
                            fullWidth
                            margin="normal"
                            variant="outlined"
                            label="Contract Sign Date"
                            type="date"
                            // defaultValue="2020-01-01"

                        />
                        <TextField
                            {...register('contract_end_date', { required: 'Contract End Date is required!' })}
                            fullWidth
                            margin="normal"
                            variant="outlined"
                            label="Contract End Date"
                            type="date"
                            // defaultValue="2022-01-01"
                        />

                    </DialogContent>
                    <DialogActions>
                        <Button onClick={() => {}} color="primary" variant="contained" type="submit">
                            {isEditMode ? 'Update' : 'Save'}
                        </Button>
                        <Button onClick={() => setOpenDialog(false)} color="secondary" variant="contained">
                            Close
                        </Button>
                    </DialogActions>
                </form>
            </Dialog>
            {/* add/update dialog/model */}


            {/* table */}
            <TableContainer component={Paper} style={{ marginTop: '20px' }}>
                <Table aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <StyledTableCell align="left">Company Name</StyledTableCell>
                            <StyledTableCell align="left">Payment Status</StyledTableCell>
                            <StyledTableCell align="left">Contract Sign Date</StyledTableCell>
                            <StyledTableCell align="left">Contract End Date</StyledTableCell>
                            <StyledTableCell align="left">Actions</StyledTableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>

                        {contracts && contracts?.map((row) => (
                            <TableRow key={row._id}>
                                <StyledTableCell align="left">{row?.client_id?.company_name}</StyledTableCell>
                                <StyledTableCell align="left">
                                    {row.payment_status === 'paid' ? 
                                        <Chip size="medium" label={row.payment_status} color="primary" />
                                        : 
                                        <Chip size="medium" label={row.payment_status} color="secondary" />
                                    }
                                </StyledTableCell>
                                <StyledTableCell align="left">
                                    <Moment format="MMMM Do YYYY">{row.contract_sign_date}</Moment>
                                </StyledTableCell>
                                <StyledTableCell align="left">
                                    <Moment fromNow>{row.contract_end_date}</Moment>
                                </StyledTableCell>
                                <StyledTableCell align="left" className={classes.icon}>
                                    <PanTool fontSize="small" onClick={() => {handleEdit(row)}} />
                                    <Delete fontSize="medium" onClick={() => {setDeleteId(row._id);setOpenDeleteDialog(true);}} />
                                </StyledTableCell>
                            </TableRow>
                        ))}

                    </TableBody>
                </Table>
            </TableContainer>
            {/* table */}


            <ConfirmDelete 
                handleDelete={handleDelete}
                setOpenDeleteDialog={setOpenDeleteDialog}
                openDeleteDialog={openDeleteDialog}
            />


        </Box>
    )
}

export default Contract
