import { makeStyles } from "@material-ui/core";

export default makeStyles((theme) => ({
  grid: {
    height: "100vh",
    width: "100vw",
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
    background: "white",
  },
  sidebar: {
    height: "100vh",
    width: "15%",
    // transition: "width .3s ease-out",
  },
  smallSidebar: {
    width: "5%",
    transition: "all .2s ease ",
  },
  // normal
  mainContent: {
    width: "85%",
    // transition: "all .2s",
  },
  // when small menu appears it is active
  bigMainContent: {
    width: "95%",
    transform: "none",
    opacity: 1,
    transitionDelay: "300ms",
    // transition: "width .01s ease-in",
  },
}));
