import {
  Box,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  MenuItem,
  TextField,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableBody,
  Paper,
  Chip,
  IconButton,
  FormHelperText,
} from "@material-ui/core";
import { useState } from "react";
// utils
import fetcher from "../../../utils/fetcher";
// library
import useSWR, { useSWRConfig } from "swr";
import { Controller, useForm } from "react-hook-form";
import axios from "axios";
import { useSnackbar } from "notistack";
import Moment from "react-moment";
import moment from "moment";
// icons
import { Delete, Edit } from "@material-ui/icons";
// styles
import StyledTableCell from "../Partials/StyledTableCell";
import ConfirmDelete from "../Partials/ConfirmDelete";
import ReactForm from "./ReactForm";

interface ClientType {
  _id: string;
  company_name: string;
}
interface ContractType<G = string> {
  _id: string;
  client_id: G;
  contract_end_date: string;
  contract_sign_date: string;
  payment_status: string;
}

const Contract = () => {
  // styles config
  const { enqueueSnackbar } = useSnackbar();

  // swr config
  const { mutate } = useSWRConfig();
  const CLIENT_API = "api/client/name";
  const CONTRACT_API = "api/contract/";

  // state
  const [openDialog, setOpenDialog] = useState<boolean>(false);
  const [isEditMode, setIsEditMode] = useState<boolean>(false);
  const [updateId, setUpdateId] = useState<string>(null);
  const [openDeleteDialog, setOpenDeleteDialog] = useState<boolean>(false);
  const [selectPaymentValue, setSelectPaymentValue] =
    useState<string>(undefined);
  const [selectClientValue, setSelectClientValue] = useState<string>(undefined);

  // date today and one year after
  const signDate: string = moment().format("yyyy-MM-DD").toString();
  const endDate: string = moment().add(1, "y").format("yyyy-MM-DD").toString();

  // hook form default date values
  const defaultValues = {
    contract_sign_date: signDate,
    contract_end_date: endDate,
    client_id: selectClientValue ? selectClientValue : undefined,
    payment_status: selectPaymentValue ? selectPaymentValue : undefined,
  };

  // hook form
  const {
    register,
    handleSubmit,
    reset,
    control,
    formState: { errors },
  } = useForm<ContractType>({
    defaultValues: defaultValues,
  });

  // FETCH DATA
  const { data: clients, error: clientError } = useSWR(CLIENT_API, fetcher);
  const { data: contracts, error: contractError } = useSWR(
    CONTRACT_API,
    fetcher
  );

  if (clientError) return <div>failed to load</div>;
  if (!clients) return <div>loading...</div>;
  if (contractError) return <div>failed to load</div>;
  if (!contracts) return <div>loading...</div>;

  const onSubmit = (data: ContractType) => {
    console.log("ob submit");
    console.log(data);
    if (!isEditMode)
      axios
        .post(CONTRACT_API, data)
        .then(({ data }) => {
          mutate(CONTRACT_API);
          setOpenDialog(false);
          enqueueSnackbar(data.success_msg, { variant: "success" });
        })
        .catch((err) => {
          console.log(err);
        });
    else {
      console.log(data);
      axios
        .patch(CONTRACT_API + updateId, data)
        .then(({ data }) => {
          mutate(CONTRACT_API);
          setOpenDialog(false);
          setIsEditMode(false);
          enqueueSnackbar(data.success_msg, { variant: "success" });
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  const handleEdit = (row: ContractType<ClientType>) => {
    console.log("inside rdidt");
    reset({
      payment_status: row.payment_status,
      client_id: row.client_id._id,
      contract_sign_date: row.contract_sign_date.split("T")[0],
      contract_end_date: row.contract_end_date.split("T")[0],
    });
    setOpenDialog(true);
    setIsEditMode(true);
    console.log(defaultValues);
  };

  const handleDelete = () => {
    axios
      .delete(CONTRACT_API + updateId)
      .then(({ data }) => {
        console.log(data);
        mutate(CONTRACT_API);
        setOpenDeleteDialog(false);
        enqueueSnackbar(data.success_msg, { variant: "success" });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleAddContract = () => {
    console.log("add");
    setSelectPaymentValue(undefined);
    setSelectClientValue(undefined);
    setIsEditMode(false);
    setOpenDialog(true);
    reset(defaultValues);
  };

  const handleClose = () => {
    setIsEditMode(false);
    setOpenDialog(false);
    setSelectPaymentValue(undefined);
    reset({});
  };

  console.log(errors);
  return (
    <Box m={5}>
      <Button color="primary" variant="outlined" onClick={handleAddContract}>
        Add Contract
      </Button>

      <ReactForm
        openDialog={openDialog}
        setOpenDialog={setOpenDialog}
        isEditMode={isEditMode}
        handleClose={handleClose}
        errors={errors}
        control={control}
        register={register}
        handleSubmit={handleSubmit}
        onSubmit={onSubmit}
        clients={clients}
      />

      {/* table */}
      <TableContainer component={Paper} style={{ marginTop: "20px" }}>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow>
              <StyledTableCell align="left">Company Name</StyledTableCell>
              <StyledTableCell align="left">Payment Status</StyledTableCell>
              <StyledTableCell align="left">Contract Sign Date</StyledTableCell>
              <StyledTableCell align="left">Contract End Date</StyledTableCell>
              <StyledTableCell align="left">Actions</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {contracts &&
              contracts?.map((row) => (
                <TableRow key={row._id} hover={true}>
                  <StyledTableCell align="left">
                    {row?.client_id?.company_name}
                  </StyledTableCell>
                  <StyledTableCell align="left">
                    {row.payment_status === "paid" ? (
                      <Chip
                        size="medium"
                        label={row.payment_status}
                        color="primary"
                      />
                    ) : (
                      <Chip
                        size="medium"
                        label={row.payment_status}
                        color="secondary"
                      />
                    )}
                  </StyledTableCell>
                  <StyledTableCell align="left">
                    <Moment format="MMMM Do YYYY">
                      {row.contract_sign_date}
                    </Moment>
                  </StyledTableCell>
                  <StyledTableCell align="left">
                    <Moment fromNow>{row.contract_end_date}</Moment>
                  </StyledTableCell>
                  <StyledTableCell align="left">
                    <IconButton>
                      <Edit
                        fontSize="medium"
                        onClick={() => {
                          setUpdateId(row._id);
                          console.log(row);
                          handleEdit(row);
                        }}
                      />
                    </IconButton>

                    <IconButton>
                      <Delete
                        fontSize="medium"
                        onClick={() => {
                          setUpdateId(row._id);
                          setOpenDeleteDialog(true);
                        }}
                      />
                    </IconButton>
                  </StyledTableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </TableContainer>
      {/* table */}

      <ConfirmDelete
        handleDelete={handleDelete}
        setOpenDeleteDialog={setOpenDeleteDialog}
        openDeleteDialog={openDeleteDialog}
      />
    </Box>
  );
};

export default Contract;
