import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, InputLabel, MenuItem, Paper, Select, Table, TableBody, TableContainer, TableHead, TableRow, TextField } from "@material-ui/core"
import { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import axios from 'axios';
import StyledTableCell from "../Partials/StyledTableCell";
import { useSnackbar } from 'notistack';
// icons
import { PanTool, Delete } from '@material-ui/icons'

interface ClientType{
    _id?:string,
    company_name:string,
    address?:string,
}

const Branch = () => {

    useEffect(() => {
        axios.get('api/client/name')
            .then(({data}) => {console.log(data);setClients(data)});
        getAllBranches();
    },[]);

    const [open, setOpen] = useState<boolean>(false);
    const [clients , setClients] = useState<ClientType[]>(null);

    const [age, setAge] = useState<number>();

    const { register, handleSubmit, reset, formState: { errors } } = useForm();

    const onSubmit = (data:ClientType) => {
        console.log(data);
        axios.post('api/branch',data)
            .then((response) => {console.log(response)})
            .catch((err) => {console.log(err)});
    }

    const handleChange = (event: React.ChangeEvent<{ value: unknown }>) => {};

    const handleClose = () => {
        setOpen(false);
    };

    const getAllBranches = () => { 
        axios.get('api/branches')
            .then(({data}) => setClients(data))
            .catch((err) => console.log(err));
    }

    return (

        <Box m={5}>

            <Button color="primary" variant="outlined" onClick={() => setOpen(true)}>Add Branch</Button>

            <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">Add New Branch</DialogTitle>

                <form onSubmit={handleSubmit(onSubmit)}>
                    <DialogContent>

                        <InputLabel id="label" >Client</InputLabel>
                        <Select {...register("client_id",{required:"Client is required!"})} labelId="label" id="select" fullWidth variant="outlined">
                            {clients && clients?.map(client => (
                                <MenuItem key={client._id} value={client._id}> {client.company_name} </MenuItem>
                            ))}
                        </Select>

                        {/* <TextField id="select" label="Client" value="20" select fullWidth variant="outlined">
                            <MenuItem value="10">Ten</MenuItem>
                            <MenuItem value="20">Twenty</MenuItem>
                        </TextField> */}

                        <TextField {...register("address", { required: "Address is requied!" })}
                            autoFocus variant="outlined" margin="normal" id="address" label="Address" type="text" fullWidth 
                        />

                    </DialogContent>

                    <DialogActions>
                        <Button color="primary" type="submit">
                            Save
                        </Button>
                        <Button onClick={() => {setOpen(false);reset({});}} color="primary">
                            Close
                        </Button>
                    </DialogActions>
                </form>

            </Dialog>


            <TableContainer component={Paper} style={{ marginTop: '20px' }}>
                <Table aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <StyledTableCell align="left">Company Name</StyledTableCell>
                            <StyledTableCell align="left">Address</StyledTableCell>
                            <StyledTableCell align="left">Actions</StyledTableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {clients && clients?.map((row) => (
                            <TableRow key={row._id}>
                                <StyledTableCell align="left">{row.company_name}</StyledTableCell>
                                <StyledTableCell align="left">{row.address}</StyledTableCell>


                              /
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

        </Box>
    )
}

export default Branch
