import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  FormHelperText,
  MenuItem,
  TextField,
} from "@material-ui/core";
import { Controller } from "react-hook-form";

const ReactForm = ({ openDialog, setOpenDialog, isEditMode, clients }) => {
  // hook form default date values
  const defaultValues = {
    contract_sign_date: signDate,
    contract_end_date: endDate,
    client_id: selectClientValue ? selectClientValue : undefined,
    payment_status: selectPaymentValue ? selectPaymentValue : undefined,
  };

  // hook form
  const {
    register,
    handleSubmit,
    reset,
    control,
    formState: { errors },
  } = useForm<ContractType>({
    defaultValues: defaultValues,
  });
  return (
    <Dialog
      open={openDialog}
      onClose={handleClose}
      aria-labelledby="form-dialog-title"
    >
      <DialogTitle id="form-dialog-title">
        {isEditMode ? "Update Contract" : "Add Contract"}
      </DialogTitle>

      <form onSubmit={handleSubmit(onSubmit)}>
        <DialogContent>
          <Controller
            name="client_id"
            control={control}
            rules={{ required: "Client is required!" }}
            render={({ field }) => (
              <TextField
                select
                {...field}
                style={{ marginBottom: "10px" }}
                label="Client"
                fullWidth
                variant="outlined"
                error={!!errors.client_id}
              >
                {clients &&
                  clients.map((client) => (
                    <MenuItem key={client._id} value={client._id}>
                      {client.company_name}
                    </MenuItem>
                  ))}
              </TextField>
            )}
          />
          <FormHelperText className="MuiFormHelperText-root MuiFormHelperText-contained Mui-error">
            {errors && errors.client_id && errors.client_id.message}
          </FormHelperText>

          <Controller
            name="payment_status"
            rules={{ required: "Payment Status is required!" }}
            control={control}
            render={({ field }) => (
              <TextField
                {...field}
                select
                label="Payment Status"
                fullWidth
                variant="outlined"
                error={!!errors.payment_status}
              >
                <MenuItem value="paid">Paid</MenuItem>
                <MenuItem value="notpaid">Not Paid</MenuItem>
              </TextField>
            )}
          />
          <FormHelperText className="MuiFormHelperText-root MuiFormHelperText-contained Mui-error">
            {errors && errors.payment_status && errors.payment_status.message}
          </FormHelperText>

          <TextField
            {...register("contract_sign_date", {
              required: "Contract Sign Date is required!",
            })}
            fullWidth
            margin="normal"
            variant="outlined"
            label="Contract Sign Date"
            type="date"
            error={!!errors.contract_sign_date}
            helperText={
              errors &&
              errors.contract_sign_date &&
              errors.contract_sign_date.message
            }
          />
          <TextField
            {...register("contract_end_date", {
              required: "Contract End Date is required!",
            })}
            fullWidth
            margin="normal"
            variant="outlined"
            label="Contract End Date"
            type="date"
            error={!!errors.contract_end_date}
            helperText={
              errors &&
              errors.contract_end_date &&
              errors.contract_end_date.message
            }
          />
        </DialogContent>
        <DialogActions>
          <Button
            onClick={() => {}}
            color="primary"
            variant="contained"
            type="submit"
          >
            {isEditMode ? "Update" : "Save"}
          </Button>
          <Button
            onClick={handleClose}
            // onClick={() => {
            //   setOpenDialog(false);
            //   reset({});
            // }}
            color="secondary"
            variant="contained"
          >
            Close
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
};

export default ReactForm;
