import {
  Box,
  Button,
  Chip,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  MenuItem,
  Paper,
  Table,
  TableBody,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@material-ui/core";
import { useEffect, useState } from "react";
// library
import useSWR, { useSWRConfig } from "swr";
import axios from "axios";
import { useForm, useWatch } from "react-hook-form";
import { useSnackbar } from "notistack";
// utils
import fetcher from "../../../utils/fetcher";
// icon
import { Delete, PanTool } from "@material-ui/icons";
// styles
import StyledTableCell from "../Partials/StyledTableCell";
import useStyles from "../Partials/CommonStyles";
import ConfirmDelete from "../Partials/ConfirmDelete";

interface BranchType {
  _id: string;
  address: string;
  client_id: string;
}

interface ContactPersonType {
  client_id: string;
  branch_id?: string;
  name: string;
  email: string;
  phone: string;
  address: string;
}
interface EditContactPersonType {
  client_id: ClientType;
  branch_id?: EditBranchType;
  name: string;
  email: string;
  phone: string;
  address: string;
}
interface ClientType {
  _id: string;
  company_name: string;
}
interface EditBranchType {
  _id: string;
  address: string;
}

const ContactPerson = () => {
  // initial config
  const classes = useStyles();
  const { enqueueSnackbar, closeSnackbar } = useSnackbar();
  const { mutate } = useSWRConfig();

  // api constants
  const CLIENT_API = "api/client/name";
  const CONTACT_API = "api/person/";

  // state
  const [openDialog, setOpenDialog] = useState<boolean>(false);
  const [isEditMode, setIsEditMode] = useState<boolean>(false);
  const [hasBranch, setHasBranch] = useState<boolean>(false);
  const [branch, setBranch] = useState<BranchType[]>(null);
  const [selectClient, setSelectClient] = useState<string>(null);
  const [selectBranch, setSelectBranch] = useState<string>(null);
  const [openDeleteDialog, setOpenDeleteDialog] = useState<boolean>(false);
  const [deleteId, setDeleteId] = useState<string>(null);
  // react hook form
  const {
    register,
    handleSubmit,
    reset,
    control,
    formState: { errors },
  } = useForm<ContactPersonType>({
    shouldUnregister: true,
  });

  const branchWatch = useWatch({ control, name: "branch_id" });

  // swr api fetch
  const { data: clients, error: clientsError } = useSWR(CLIENT_API, fetcher);
  const { data: contacts, error: contactsError } = useSWR(CONTACT_API, fetcher);
  if (clientsError) return <div>failed to load</div>;
  if (!clients) return <div>loading...</div>;
  if (contactsError) return <div>failed to load</div>;
  if (!contacts) return <div>loading...</div>;

  const onSubmit = (data) => {
    // console.log(data);
    setSelectBranch(null);
    if (!isEditMode)
      axios
        .post(CONTACT_API, data)
        .then((response) => {
          console.log(response);
          mutate(CONTACT_API);
        })
        .catch((error) => console.log(error));
    else {
      console.log(data);
      axios
        .patch(CONTACT_API + data._id, data)
        .then(({ data }) => {
          mutate(CONTACT_API);
          enqueueSnackbar(data.success_msg, { variant: "success" });
        })
        .catch((error) =>
          enqueueSnackbar(error.message, { variant: "warning" })
        );
    }
  };

  const handleClose = () => {
    setOpenDialog(false);
  };

  const handleSelectChange = (event: any) => {
    const id = event.target.value;
    getBranchOfClient(id);
  };

  const getBranchOfClient = (id: string) => {
    axios
      .get("/api/branch/client/" + id)
      .then(({ data }) => {
        console.log(data);
        if (data.length) {
          setHasBranch(true);
          setBranch(data);
        } else {
          setHasBranch(false);
          setBranch(null);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleEdit = (row: EditContactPersonType) => {
    setHasBranch(false);
    setIsEditMode(true);
    // console.log(row);
    if (row.branch_id) {
      getBranchOfClient(row.client_id._id);
      setSelectBranch(row.branch_id._id);
    }
    reset(row);
    setOpenDialog(true);
    setSelectClient(row.client_id._id);
  };

  const handleAddContact = () => {
    reset({});
    setOpenDialog(true);
    setIsEditMode(false);
    setSelectClient(null);
    setSelectBranch(null);
  };

  const handleDelete = () => {
    console.log("delete");
    axios
      .delete("api/person/" + deleteId)
      .then(({ data }) => {
        setOpenDeleteDialog(false);
        mutate(CONTACT_API);
        enqueueSnackbar(data.success_msg, { variant: "success" });
      })
      .catch((err) => enqueueSnackbar(err.message, { variant: "warning" }));
  };

  return (
    <Box m={5}>
      <Button variant="outlined" color="primary" onClick={handleAddContact}>
        Add Contact Person
      </Button>

      <Dialog
        open={openDialog}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">
          {isEditMode ? "Update" : "Add"} Contact Person
        </DialogTitle>
        <form onSubmit={handleSubmit(onSubmit)}>
          <DialogContent>
            <TextField
              select
              {...register("client_id")}
              onChange={handleSelectChange}
              label="Client"
              fullWidth
              defaultValue={selectClient}
              variant="outlined"
            >
              {clients.map((client: ClientType) => (
                <MenuItem key={client._id} value={client._id}>
                  {client.company_name}
                </MenuItem>
              ))}
            </TextField>

            {hasBranch && (
              <TextField
                select
                {...register("branch_id")}
                label="Branch"
                fullWidth
                variant="outlined"
                style={{ marginTop: "20px" }}
                defaultValue={selectBranch}
              >
                {branch?.map((row) => (
                  <MenuItem key={row._id} value={row._id}>
                    {row.address}
                  </MenuItem>
                ))}
              </TextField>
            )}

            <TextField
              variant="outlined"
              margin="normal"
              {...register("name")}
              label="Name"
              type="text"
              fullWidth
            />
            <TextField
              variant="outlined"
              margin="normal"
              {...register("email")}
              label="Email Address"
              type="email"
              fullWidth
            />
            <TextField
              variant="outlined"
              margin="normal"
              {...register("address")}
              label="Address"
              type="text"
              fullWidth
            />
            <TextField
              variant="outlined"
              margin="normal"
              {...register("phone")}
              label="Phone Number"
              type="text"
              fullWidth
            />
          </DialogContent>

          <DialogActions>
            <Button
              onClick={handleClose}
              color="primary"
              variant="contained"
              type="submit"
            >
              {isEditMode ? "Update" : "Save"}
            </Button>
            <Button onClick={handleClose} color="secondary" variant="contained">
              Close
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      <TableContainer component={Paper} style={{ marginTop: "20px" }}>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow>
              <StyledTableCell align="left">Company Name</StyledTableCell>
              <StyledTableCell align="left">Branch Address</StyledTableCell>
              <StyledTableCell align="left">Name</StyledTableCell>
              <StyledTableCell align="left">Email</StyledTableCell>
              <StyledTableCell align="left">Phone</StyledTableCell>
              <StyledTableCell align="left">Address</StyledTableCell>
              <StyledTableCell align="left">Actions</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {contacts?.map((row) => (
              <TableRow key={row._id}>
                <StyledTableCell align="left">
                  {row.client_id.company_name}
                </StyledTableCell>
                <StyledTableCell align="left">
                  {row.branch_id?.address ? (
                    row.branch_id.address
                  ) : (
                    <Chip
                      label="No Branch"
                      variant="outlined"
                      color="secondary"
                    />
                  )}
                </StyledTableCell>
                <StyledTableCell align="left">{row.name}</StyledTableCell>
                <StyledTableCell align="left">{row.email}</StyledTableCell>
                <StyledTableCell align="left">{row.phone}</StyledTableCell>
                <StyledTableCell align="left">{row.address}</StyledTableCell>

                <StyledTableCell align="left" className={classes.icon}>
                  <PanTool
                    fontSize="small"
                    onClick={() => {
                      console.log(row);
                      setDeleteId(row._id);
                      handleEdit(row);
                    }}
                  />
                  <Delete
                    fontSize="medium"
                    onClick={() => {
                      setDeleteId(row._id);
                      setOpenDeleteDialog(true);
                    }}
                  />
                </StyledTableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <ConfirmDelete
        openDeleteDialog={openDeleteDialog}
        setOpenDeleteDialog={setOpenDeleteDialog}
        handleDelete={handleDelete}
      />
    </Box>
  );
};

export default ContactPerson;
