import React, { useContext } from 'react';
import './App.css';
import Home from './pages/Home';
import { AuthenticationContext} from './AuthenticationProvider';
import Dashboard from './pages/Dashboard';
import { Redirect, useHistory } from 'react-router';

function App() {
  const history = useHistory();
  const valueOf = useContext(AuthenticationContext);
  const status = valueOf.authenticationStatus;
  // console.log(valueOf);
  // console.log(valueOf.authenticationStatus);
if(status === 'uncertain') return <>Loading</>

  if (status === 'loggedIn' ) history.replace('/');
  else if (status === 'loggedOut') return history.replace('/login');

  else return (<Switch>

    <Route path="/register">
      <Register />
    </Route>
    <Route path="/login">
      <Login />
    </Route>
    <Route path="/" >
      <Dashboard />
    </Route>

  </Switch>);


}

export default App;
