import { AppBar, Badge, IconButton, InputBase, Toolbar, Tooltip, Typography } from "@material-ui/core";
import axios from 'axios'
import {useHistory} from 'react-router-dom';
// icons
import SearchIcon from '@material-ui/icons/Search';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MailIcon from '@material-ui/icons/Mail';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import MoreIcon from '@material-ui/icons/MoreVert';
import useStyles from './NavbarStyles'
// authentication context
import {useContext} from 'react';
import {AuthenticationContext} from '../../AuthenticationProvider';

const Navbar = () => {

    const {authenticationStatus, setAuthenticationStatus} = useContext(AuthenticationContext);

    const classes = useStyles();
    const history = useHistory();

    const handleLogout =  () => {
        axios.get('auth/logout')
            .then((response) => { setAuthenticationStatus('loggedOut'); history.push('/'); });

    }


    return (
        <div>
            <AppBar position="static" color="transparent">
                <Toolbar>

                    <Typography className={classes.title} variant="h6" noWrap>
                       HOME
                    </Typography>
                    <div className={classes.search}>
                        <div className={classes.searchIcon}>
                            <SearchIcon />
                        </div>
                        <InputBase
                            placeholder="Search…"
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search' }}
                        />
                    </div>
                    <div className={classes.grow} />
                    <div className={classes.sectionDesktop}>
                        <IconButton aria-label="show 4 new mails" color="inherit">
                            <Badge badgeContent={4} color="secondary">
                                <MailIcon />
                            </Badge>
                        </IconButton>
                        <IconButton aria-label="show 17 new notifications" color="inherit">
                            <Tooltip title="Logout" aria-label="logout">
                                <ExitToAppIcon onClick={handleLogout} />
                            </Tooltip>
                        </IconButton>
                        <IconButton
                            edge="end"
                            aria-label="account of current user"
                            aria-haspopup="true"
                            color="inherit"
                        >
                            <AccountCircle />
                        </IconButton>
                    </div>
                    <div className={classes.sectionMobile}>
                        <IconButton
                            aria-label="show more"
                            aria-haspopup="true"
                            color="inherit"
                        >
                            <MoreIcon />
                        </IconButton>
                    </div>
                </Toolbar>
            </AppBar>
        </div>
    )
}

export default Navbar
