import React, { useContext, useEffect } from 'react';
import './App.css';
import Home from './pages/Home';
import { AuthenticationContext } from './AuthenticationProvider';
import Dashboard from './pages/Dashboard';
import { Route, Switch, useHistory } from 'react-router';
import Register from './pages/Register';
import Login from './pages/Login';

function App() {

  const history = useHistory();

  const valueOf = useContext(AuthenticationContext);
  const status = valueOf.authenticationStatus;
  // console.log(valueOf);
  // console.log(valueOf.authenticationStatus);


  useEffect(() => {
    if (status === 'loggedIn') history.replace('/');
    else if (status === 'loggedOut') history.replace('/login');
  }, [status, history]);


  if (status === 'uncertain') return <>Loading</>;

  else return (

      <Switch>
        <Route path="/register">
          <Register />
        </Route>
        <Route path="/login">
          <Login />
        </Route>
        <Route path="/" >
          <Dashboard />
        </Route>

      </Switch>
  );


}

export default App;
