import {
  Box,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  Table,
  TableBody,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@material-ui/core";
import { useState } from "react";
// library
import { useForm, Controller } from "react-hook-form";
import axios from "axios";
import { useSnackbar } from "notistack";
// styles
import StyledTableCell from "../Partials/StyledTableCell";
import useStyles from "../Partials/CommonStyles";
// icons
import { PanTool, Delete } from "@material-ui/icons";
// swr
import useSWR, { useSWRConfig } from "swr";
import fetcher from "../../../utils/fetcher";
import ConfirmDelete from "../Partials/ConfirmDelete";
// import ReactHookFormSelect from "./ReactHookFormSelect";

interface ClientType {
  _id?: string;
  company_name: string;
}
interface BranchType {
  _id?: string;
  address?: string;
  client_id?: ClientType;
}

const Branch = () => {
  const CLIENT_API = "api/client/name";
  const BRANCH_API = "api/branch";

  const classes = useStyles();
  // notification snackbar
  const { enqueueSnackbar } = useSnackbar();
  const { mutate } = useSWRConfig();

  // state
  const [open, setOpen] = useState<boolean>(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState<boolean>(false);
  const [deleteId, setDeleteId] = useState<string>(null);
  const [isEditMode, setIsEditMode] = useState<boolean>(false);
  const [selectValue, setSelectValue] = useState<string>(""); // for select element value in edit mode
  // form hook
  const {
    register,
    handleSubmit,
    reset,
    control,
    setValue,
    formState: { errors },
  } = useForm({ defaultValues: { client_id: "" } });

  // swr config
  const { data: clients, error: clientError } = useSWR(CLIENT_API, fetcher);
  const { data: branches, error: branchError } = useSWR(BRANCH_API, fetcher);

  if (clientError) return <div>failed to load client data</div>;
  if (!clients) return <div>loading...</div>;
  if (branchError) return <div>failed to load branch data</div>;
  if (!branches) return <div>loading...</div>;

  console.log(clients);
  console.log(branches);

  // save new branch and also update
  const onSubmit = (data: BranchType) => {
    console.log(data);
    if (!isEditMode)
      axios
        .post("api/branch", data)
        .then(({ data }) => {
          setOpen(false);
          mutate(BRANCH_API);
          enqueueSnackbar(data.success_msg, { variant: "success" });
        })
        .catch((err) => {
          console.log(err);
        });
    else
      axios
        .patch("api/branch/" + data._id, data)
        .then(({ data }) => {
          mutate(BRANCH_API);
          enqueueSnackbar(data.success_msg, { variant: "success" });
          setOpen(false);
        })
        .catch((err) => {
          console.log(err);
        });
  };

  // when dialog closes
  const handleClose = () => {
    setOpen(false);
  };

  // edit config
  const handleEdit = (row: BranchType) => {
    setIsEditMode(true);
    setOpen(true);
    // setSelectValue(row.client_id._id);
    reset(row);
    setValue("client_id", row.client_id._id);
  };

  const handleDelete = () => {
    axios
      .delete("api/branch/" + deleteId)
      .then(({ data }) => {
        mutate(BRANCH_API);
        setOpenDeleteDialog(false);
        enqueueSnackbar(data.success_msg, { variant: "success" });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <Box m={5}>
      <Button color="primary" variant="outlined" onClick={() => setOpen(true)}>
        Add Branch
      </Button>

      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">
          {isEditMode ? "Edit Branch" : "Add New Branch"}
        </DialogTitle>

        <form onSubmit={handleSubmit(onSubmit)}>
          <DialogContent>
            {/* <InputLabel id="label" >Client</InputLabel>
                        <Select {...register("client_id",{required:"Client is required!"})}
                            labelId="label" id="select" fullWidth variant="outlined"
                            defaultValue={selectValue}
                        >
                            {clients && clients?.map(client => (
                                <MenuItem key={client._id} value={client._id}> {client.company_name} </MenuItem>
                            ))}
                        </Select> */}

            <ReactHookFormSelect
              name="client_id"
              label="Client"
              control={control}
              variant="outlined"
              margin="normal"
              defaultValue=""
              fullWidth
            >
              {clients &&
                clients?.map((client) => (
                  <MenuItem key={client._id} value={client._id}>
                    {" "}
                    {client.company_name}{" "}
                  </MenuItem>
                ))}
            </ReactHookFormSelect>

            {/* <Controller
                            as={
                                <Select>
                                    {clients && clients.map((option, index) => (
                                        <MenuItem key={index} value={option._id}>
                                            {option.company_name}
                                        </MenuItem>
                                    ))}
                                </Select>
                            }
                            name="client_id"
                            control={control}
                            defaultValue=""
                        /> */}

            <TextField
              {...register("address", { required: "Address is requied!" })}
              autoFocus
              variant="outlined"
              margin="normal"
              id="address"
              label="Address"
              type="text"
              fullWidth
            />
          </DialogContent>

          <DialogActions>
            <Button color="primary" variant="contained" type="submit">
              {isEditMode ? "Update" : "Save"}
            </Button>
            <Button
              onClick={() => {
                setOpen(false);
                reset({});
              }}
              color="secondary"
              variant="contained"
            >
              Close
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      <TableContainer component={Paper} style={{ marginTop: "20px" }}>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow>
              <StyledTableCell align="left">Company Name</StyledTableCell>
              <StyledTableCell align="left">Address</StyledTableCell>
              <StyledTableCell align="left">Actions</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {branches &&
              branches?.map((row) => (
                <TableRow key={row._id}>
                  <StyledTableCell align="left">
                    {row?.client_id?.company_name}
                  </StyledTableCell>
                  <StyledTableCell align="left">{row.address}</StyledTableCell>

                  <StyledTableCell align="left" className={classes.icon}>
                    <PanTool fontSize="small" onClick={() => handleEdit(row)} />
                    <Delete
                      fontSize="medium"
                      onClick={() => {
                        setDeleteId(row._id);
                        setOpenDeleteDialog(true);
                      }}
                    />
                  </StyledTableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </TableContainer>

      <ConfirmDelete
        openDeleteDialog={openDeleteDialog}
        setOpenDeleteDialog={setOpenDeleteDialog}
        handleDelete={handleDelete}
      />
    </Box>
  );
};

export default Branch;
