import React, { useContext, useEffect } from "react";
import "./App.css";
import { AuthenticationContext } from "./AuthenticationProvider";
import Dashboard from "./pages/Dashboard";
import { Route, Switch, useHistory } from "react-router";
import Register from "./pages/Register";
import Login from "./pages/Login";
import PublicRoute from "./PublicRoute";
import PrivateRoute from "./PrivateRoute";

function App() {
  const history = useHistory();
  const valueOf = useContext(AuthenticationContext);
  const status = valueOf.authenticationStatus;
  console.log(status);
  // console.log(valueOf.authenticationStatus);

  // useEffect(() => {
  //   if (status === "loggedIn") history.replace("/");
  //   else if (status === "loggedOut") history.replace("/login");
  // }, [status, history]);

  if (status === "uncertain") return <>Loading</>;
  else
    return (
      <div className="App">
        <Switch>
          <PublicRoute
            status={status}
            restricted={false}
            path="/register"
            component={Register}
          />
          <PublicRoute
            status={status}
            restricted={false}
            path="/login"
            component={Login}
          />
          <PrivateRoute status={status} path="/" component={Dashboard} />
        </Switch>
      </div>
    );
}

export default App;
