import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, MenuItem, TextField } from "@material-ui/core"
import { useEffect, useState } from "react";
import useSWR from 'swr';
import axios from 'axios';
import fetcher from '../../../utils/fetcher';
import { useForm } from "react-hook-form";

interface BranchType{
    _id: string;
    address: string;
    client_id: string;
}

interface ContactPersonType{
    client_id: string;
    branch_id?: string;
    name: string;
    email: string;
    phone: string;
    address: string;
}

const ContactPerson = () => {

    const CLIENT_API = 'api/client/name';
    const CONTACT_API = 'api/person';

    // state
    const [ openDialog,setOpenDialog] = useState<boolean>(false);
    const [hasBranch,setHasBranch] = useState<boolean>(false);
    const [branch, setBranch] = useState<BranchType[]>(null);
    // react hook form
    const { register, handleSubmit, watch, formState: { errors } } = useForm<ContactPersonType>();


    // swr api fetch
    const { data: clients, error: clientsError } = useSWR(CLIENT_API, fetcher);
    const { data: contacts, error: contactsError } = useSWR(CONTACT_API, fetcher);
    if (clientsError) return <div>failed to load</div>
    if (!clients) return <div>loading...</div>
    if (contactsError) return <div>failed to load</div>
    if (!contacts) return <div>loading...</div>


    const onSubmit = data => {
        console.log(data)
    };


    const handleClose = () => {setOpenDialog(false);}

    const handleSelectChange =(event:any) => {

        const id = event.target.value;

        axios.get('/api/branch/client/'+id)
            .then(({data}) => {
                console.log(data);
                if(data.length) {
                    setHasBranch(true);
                    setBranch(data);
                }else{
                    setHasBranch(false);
                    setBranch(null);
                }
            })
            .catch((error) => {console.log(error)});
    }

    return (
        <Box m={5}>

            <Button variant="outlined" color="primary" onClick={() => setOpenDialog(true)}>
                Add Contact Person
            </Button>

            <Dialog open={openDialog} onClose={handleClose} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">Contact Person</DialogTitle>
                 <form onSubmit={handleSubmit(onSubmit)}>
                <DialogContent>

                        <TextField select {...register("client_id")} onChange={handleSelectChange} label="Client" fullWidth  variant="outlined">
                        {clients.map((client) => (
                            <MenuItem key={client._id} value={client._id}>{client.company_name}</MenuItem>
                        ))}
                    </TextField>

                    {hasBranch &&  
                            <TextField select  {...register("branch_id")} label="Branch" fullWidth variant="outlined" style={{ marginTop: '20px' }}>
                            {branch?.map((row) => (
                                <MenuItem key={row._id} value={row._id}>{row.address}</MenuItem>
                                ))}
                        </TextField>
                    }

                    <TextField variant="outlined" margin="normal" {...register("name")} label="Name" type="text" fullWidth />
                    <TextField variant="outlined" margin="normal" {...register("email")} label="Email Address" type="email" fullWidth />
                    <TextField variant="outlined" margin="normal" {...register("address")} label="Address" type="text" fullWidth />
                    <TextField variant="outlined" margin="normal" {...register("phone")} label="Phone Number" type="text" fullWidth />
                </DialogContent>

                <DialogActions>
                    <Button onClick={handleClose} color="primary" variant="outlined" type="submit"> Save </Button>
                    <Button onClick={handleClose} color="secondary" variant="outlined">Close</Button>
                </DialogActions>
                </form>
            </Dialog>
        </Box>
    )
}

export default ContactPerson
