import { Box, TextField, Button, Typography} from '@material-ui/core'
import { Link , useHistory} from 'react-router-dom'
import { useForm } from "react-hook-form";
import useStyles from './LoginStyles';
import axios from 'axios';
import { useCookies } from 'react-cookie';
import {useState} from 'react';

interface IForm {
    email: string,
    password: string,
}

const Login = () => {

    const classes = useStyles();
    const history = useHistory();

    const { register, handleSubmit, formState: { errors } } = useForm();

    const [cookies, setCookie] = useCookies(['accessToken']);
    const [accessToken, setAccessToken] = useState<string>(null);

    // handle login form submit
    const onSubmit = async (data: IForm) => {
        console.log(data);
        const newData = await axios.post('/auth/login',data);
        console.log(newData);
        setCookie('accessToken', accessToken, { path: '/' });

        if(newData.status===200)
            history.push('/dashboard');
    }

    return (
        <Box component="form" onSubmit={handleSubmit(onSubmit)} className={classes.box} p={5} width="25%">

            <Typography className={classes.title} variant="h4">Login</Typography>

            <TextField error={errors.email && true} {...register('email',{required:"Email is required!"})} id="outlined-basic" label="Email" variant="outlined" className={classes.input}/>
            <Typography component="span" variant="caption" className={classes.error}>
                {errors.email?.type === 'required' && errors.email.message}
            </Typography>


            <TextField {...register('password',{required:"Password is required!"})} error={errors.password && true} id="outlined-basic" label="Password" variant="outlined" className={classes.input}/>
            <Typography component="span" variant="caption" className={classes.error}>
                {errors.password?.type === 'required' && errors.password.message}
            </Typography>

            <Button type="submit" className={classes.input} variant="contained" size="large" color="primary" style={{marginTop:'20px'}}>Submit</Button>

            <Typography component="span" variant="body2" className={classes.text}>No Account   <Link to="/register">Register</Link> </Typography>

        </Box>
    )
}

export default Login
