import { Box, Button, TextField } from "@material-ui/core";
import axios from "axios";
import { useContext, useEffect, useState } from "react";
// import { AuthenticationContext } from "../../../AuthenticationProvider";
import { useForm } from "react-hook-form";

interface UserType {
  name: string;
  email: string;
  password: string;
  _id?: string;
}
const Profile = () => {
  // const valueOf = useContext(AuthenticationContext);
  // console.log(valueOf);

  const [user, setUser] = useState<UserType>({
    name: "",
    password: "",
    email: "",
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  useEffect(() => {
    axios
      .get("/auth")
      .then(({ data }) => {
        console.log(data);
        setUser(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  console.log(user.name);

  return (
    <Box m={5}>
      <form onSubmit={handleSubmit(onSubmit)}>
        <TextField
          {...register("name", { required: "Full Name is required!" })}
          variant="outlined"
          margin="normal"
          label="Name"
          type="text"
          fullWidth
          value={user.name}
        />
        <TextField
          {...register("email", { required: "Email is required!" })}
          variant="outlined"
          margin="normal"
          label="Email Address"
          type="email"
          fullWidth
          value={user.email}
        />
        <TextField
          {...register("oldpassword", {
            required: "Old Password is required!",
          })}
          variant="outlined"
          margin="normal"
          label="Old Password"
          type="text"
          fullWidth
        />

        <TextField
          {...register("password", { required: "New Password is required!" })}
          variant="outlined"
          margin="normal"
          label="New Password"
          type="text"
          fullWidth
        />

        <Button
          color="primary"
          variant="contained"
          size="large"
          fullWidth
          style={{ marginTop: "10px" }}
        >
          Update
        </Button>
      </form>
    </Box>
  );
};

export default Profile;
