import { Box, TextField, makeStyles, Button, Typography, InputAdornment, IconButton } from '@material-ui/core'
import { Visibility, VisibilityOff } from '@material-ui/icons'
import { useState } from 'react';
import { Link } from 'react-router-dom'
import { useForm } from "react-hook-form";

const useStyles = makeStyles((theme) => ({
    box: {
        textAlign: 'center',
        marginTop: '-140px',
        display: 'flex',
        flexDirection: 'column',
        background: 'white',
        borderRadius: '10px',
        border: '5px solid #fafafa',
        [theme.breakpoints.down('sm')]: {
            width: '60%',
        },
    },
    title: {
        marginBottom: '20px'
    },
    input: {
        marginBottom: '10px',
    },
}));


const Login = () => {

    const [showPassword, setShowPassword] = useState<boolean>(false);

    // react form hook
    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const onSubmit = (data) => {
        console.log(data);
    }

    const handleClickShowPassword = () => {
        console.log('show pass');
        console.log(showPassword);
        setShowPassword(!showPassword);
    };

    // const handleMouseDownPassword = (event: React.MouseEvent<HTMLButtonElement>) => {
    //     event.preventDefault();
    // };

    const classes = useStyles();

    return (
            // <form onSubmit={handleSubmit(onSubmit)}>
            <Box className={classes.box} p={5} width="25%" component="form" onSubmit={handleSubmit(onSubmit)}>

                <Typography className={classes.title} variant="h4">Register</Typography>
                <TextField {...register("name")} id="outlined-basic" label="Full Name" variant="outlined" className={classes.input} />
                <TextField {...register("email")} id="outlined-basic" label="Email" variant="outlined" className={classes.input} />
                <TextField {...register("password")} id="outlined-basic" label="Password" variant="outlined" className={classes.input} 
                    type={showPassword ? 'text' : 'password'}
                    InputProps={{
                        endAdornment:(
                        <InputAdornment position="end">
                            <IconButton
                            aria-label="toggle password visibility"
                            onClick={handleClickShowPassword}
                            // onMouseDown={handleMouseDownPassword}
                            >
                            {showPassword ? <Visibility /> : <VisibilityOff />}
                            </IconButton>
                        </InputAdornment>
                        )
                    }}
                />
                <TextField {...register("confirm-[assword")} id="outlined-basic" label="Confirm Password"  variant="outlined" className={classes.input}
                    type={showPassword ? 'text' : 'password'}
                    InputProps={{ 
                        endAdornment:(
                            <InputAdornment position="end">
                            <IconButton
                                aria-label="toggle password visibility"
                                onClick={handleClickShowPassword}
                                // onMouseDown={handleMouseDownPassword}
                            >
                                {showPassword ? <Visibility /> : <VisibilityOff />}
                            </IconButton>
                            </InputAdornment>
                        )
                    }}
                />
                <Button type="submit" className={classes.title} size="large" variant="contained" color="primary" style={{ marginTop: '20px' }}>Submit</Button>


            <Typography variant="body2">Already Registered <Link to="/login">Login</Link></Typography>

        </Box>
    )
}

export default Login
